import React, { useEffect, useRef, useState } from 'react';
import '../styles/MenuSection.css';

// Türkçe karakterler için özel toLowerCase fonksiyonu
const turkishToLowerCase = (str) => {
  const turkish = {
    'İ': 'i',
    'I': 'ı',
    'Ğ': 'ğ',
    'Ü': 'ü',
    'Ş': 'ş',
    'Ö': 'ö',
    'Ç': 'ç'
  };

  return str.replace(/[İIĞÜŞÖÇ]/g, letter => turkish[letter] || letter).toLowerCase();
};

const CalorieBadge = ({ calories }) => {
  const value = parseInt(calories);
  let badgeClass = 'calorie-badge';

  if (value > 300) {
    badgeClass += ' high';
  } else if (value >= 200) {
    badgeClass += ' medium';
  } else {
    badgeClass += ' low';
  }

  return (
    <span className={badgeClass}>
      {value} kcal
    </span>
  );
};

function MenuSection({ title, menu, isToday, isTomorrow, isOtherDay }) {
  const [dateText, ...titleParts] = title.split(' - ');
  const sectionClass = isToday 
    ? 'menu-section today' 
    : isTomorrow 
      ? 'menu-section tomorrow' 
      : 'menu-section other-day';

  const menuItemsRef = useRef([]);
  const sectionRef = useRef(null);
  const [backgroundPositions, setBackgroundPositions] = useState([]);

  // Arkaplan pozisyonlarını hesapla
  useEffect(() => {
    if (sectionRef.current && menuItemsRef.current.length > 0) {
      const sectionRect = sectionRef.current.getBoundingClientRect();
      const positions = menuItemsRef.current.map(item => {
        if (!item) return { x: '50%', y: '50%' };

        const itemRect = item.getBoundingClientRect();
        const relativeX = (itemRect.left + itemRect.width / 2) - sectionRect.left;
        const relativeY = (itemRect.top + itemRect.height / 2) - sectionRect.top;

        // Arkaplan pozisyonunu hesapla
        const x = (relativeX / sectionRect.width) * 100;
        const y = (relativeY / sectionRect.height) * 100;

        return { x: `${x}%`, y: `${y}%` };
      });

      setBackgroundPositions(positions);
    }
  }, []);

  useEffect(() => {
    const observerCallback = (entries) => {
      entries.forEach((entry, index) => {
        if (entry.isIntersecting) {
          // Sıralı animasyon için gecikme ekle
          setTimeout(() => {
            entry.target.classList.add('visible');
          }, index * 100); // Her öğe için 100ms gecikme
        }
      });
    };

    const observer = new IntersectionObserver(observerCallback, {
      threshold: 0.1,
      rootMargin: '0px'
    });

    menuItemsRef.current.forEach(item => {
      if (item) {
        observer.observe(item);
      }
    });

    return () => {
      menuItemsRef.current.forEach(item => {
        if (item) {
          observer.unobserve(item);
        }
      });
    };
  }, []);

  const extractCalories = (item) => {
    const match = item.match(/(\d+)\s*KKAL/i);
    return match ? match[1] : null;
  };

  return (
    <section className={sectionClass} ref={sectionRef}>
      <div className="menu-title">
        <h2>{turkishToLowerCase(dateText)}</h2>
        {titleParts.length > 0 && <p className="date-subtitle">{turkishToLowerCase(titleParts.join(' - '))}</p>}
      </div>
      <div className="menu-items">
        {menu.items.map((item, index) => {
          const calories = extractCalories(item);
          const itemName = turkishToLowerCase(item.replace(/\s*\d+\s*KKAL/i, ''));
          const bgPosition = backgroundPositions[index] || { x: '50%', y: '50%' };

          return (
            <div 
              key={index} 
              className="menu-item fade-in"
              ref={el => menuItemsRef.current[index] = el}
              style={{
                '--bg-position-x': bgPosition.x,
                '--bg-position-y': bgPosition.y
              }}
            >
              <span className="item-name">{itemName}</span>
              {calories && <CalorieBadge calories={calories} />}
            </div>
          );
        })}
      </div>
    </section>
  );
}

export default MenuSection;
