import React from 'react';
import { format } from 'date-fns';
import { tr } from 'date-fns/locale';
import MenuSection from './MenuSection';
import '../styles/MenuList.css';

function MenuList({ menuData }) {
  // Bugünün tarihi
  const today = new Date();
  const tomorrow = new Date(today);
  tomorrow.setDate(today.getDate() + 1);

  const todayFormatted = format(today, 'd MMMM yyyy', { locale: tr });
  const tomorrowFormatted = format(tomorrow, 'd MMMM yyyy', { locale: tr });

  // Bugünün ve yarının menüsünü bul
  const todayMenu = menuData.find(menu => {
    const menuDate = new Date(menu.date);
    return menuDate.getDate() === today.getDate() && 
           menuDate.getMonth() === today.getMonth() && 
           menuDate.getFullYear() === today.getFullYear();
  });

  const tomorrowMenu = menuData.find(menu => {
    const menuDate = new Date(menu.date);
    return menuDate.getDate() === tomorrow.getDate() && 
           menuDate.getMonth() === tomorrow.getMonth() && 
           menuDate.getFullYear() === tomorrow.getFullYear();
  });

  // Diğer günlerin menülerini bul (bugün ve yarından sonraki günler)
  const otherDaysMenus = menuData.filter(menu => {
    const menuDate = new Date(menu.date);
    const twoDaysFromNow = new Date(today);
    twoDaysFromNow.setDate(today.getDate() + 2);

    // Sadece bugünden iki gün sonrası ve sonrasındaki menüleri göster
    return menuDate >= twoDaysFromNow;
  }).sort((a, b) => new Date(a.date) - new Date(b.date));

  return (
    <div className="menu-list">
      {/* Bugünün menüsü */}
      {todayMenu ? (
        <MenuSection 
          title={`bugünün menüsü - ${todayFormatted}`}
          menu={todayMenu}
          isToday={true}
        />
      ) : (
        <div className="no-menu today">
          <h2>bugünün menüsü - {todayFormatted}</h2>
          <p>Bugün için menü bilgisi bulunamadı.</p>
        </div>
      )}

      {/* Boşluk */}
      <div className="menu-section-spacer"></div>

      {/* Yarının menüsü */}
      {tomorrowMenu ? (
        <MenuSection 
          title={`yarının menüsü - ${tomorrowFormatted}`}
          menu={tomorrowMenu}
          isTomorrow={true}
        />
      ) : (
        <div className="no-menu tomorrow">
          <h2>yarının menüsü - {tomorrowFormatted}</h2>
          <p>Yarın için menü bilgisi bulunamadı.</p>
        </div>
      )}

      {/* Diğer günlerin menüleri */}
      {otherDaysMenus.length > 0 && (
        <>
          {/* Boşluk */}
          <div className="menu-section-spacer"></div>

          {/* Diğer günler başlığı */}
          <h2 className="other-days-title">diğer günler</h2>

          <div className="other-days-content">
            {otherDaysMenus.map((menu, index) => (
              <React.Fragment key={index}>
                <MenuSection 
                  title={format(new Date(menu.date), 'd MMMM yyyy', { locale: tr })}
                  menu={menu}
                  isOtherDay={true}
                />
                {index < otherDaysMenus.length - 1 && <div className="menu-section-spacer"></div>}
              </React.Fragment>
            ))}
          </div>
        </>
      )}

      {/* Footer - sayfanın en sonunda */}
      <footer className="footer">
        <p>© 2025 kastamonu üniversitesi yemekhane listesi</p>
        <p>made with ❤️ by <a href="https://github.com/kadirc4n" target="_blank" rel="noopener noreferrer">@kc4ca</a></p>
      </footer>
    </div>
  );
}

export default MenuList;
