import React, { useState, useEffect } from 'react';
import Header from './Header';
import MenuList from './MenuList';
import '../styles/App.css';
import { fetchMenuData } from '../utils/menuUtils';

function App() {
  const [menuData, setMenuData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const data = await fetchMenuData();
        setMenuData(data);
      } catch (err) {
        console.error('Veri yüklenirken hata oluştu:', err);
        setError('Menü verileri yüklenirken bir hata oluştu. Lütfen daha sonra tekrar deneyin.');
      } finally {
        setLoading(false);
      }
    };

    loadData();

    // Her gün otomatik olarak veriyi güncelle
    const interval = setInterval(loadData, 86400000); // 24 saat (ms cinsinden)

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="app">
      <Header />
      <main className="main-content">
        {loading ? (
          <div className="loading">Menü yükleniyor...</div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : (
          <MenuList menuData={menuData} />
        )}
      </main>
      <footer className="footer">
        <p>© {new Date().getFullYear()} Kastamonu Üniversitesi Yemekhane</p>
      </footer>
    </div>
  );
}

export default App;
