import React, { useEffect, useRef } from 'react';
import '../styles/Header.css';

function Header() {
  const canvasRef = useRef(null);
  const headerRef = useRef(null);

  useEffect(() => {
    const loadAndProcessImage = async () => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      const img = new Image();
      const logo = new Image();

      img.src = '/yemekhane.jpg';
      logo.src = '/kuneyesek-logo.png';

      await new Promise((resolve) => {
        img.onload = () => {
          // Set canvas size to match container
          canvas.width = headerRef.current.offsetWidth;
          canvas.height = headerRef.current.offsetHeight;

          // Calculate image dimensions to cover the canvas
          const imageAspectRatio = img.width / img.height;
          const canvasAspectRatio = canvas.width / canvas.height;

          let drawWidth = canvas.width;
          let drawHeight = canvas.height;

          if (canvasAspectRatio > imageAspectRatio) {
            drawHeight = canvas.width / imageAspectRatio;
          } else {
            drawWidth = canvas.height * imageAspectRatio;
          }

          const drawX = (canvas.width - drawWidth) / 2;
          const drawY = (canvas.height - drawHeight) / 2;

          // Draw the background image
          ctx.drawImage(img, drawX, drawY, drawWidth, drawHeight);

          // Create gradient overlay
          const gradient = ctx.createLinearGradient(0, 0, 0, canvas.height * 0.5);
          gradient.addColorStop(0, 'rgba(0, 0, 0, 0.85)');
          gradient.addColorStop(1, 'rgba(0, 0, 0, 0)');

          // Apply gradient
          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, canvas.width, canvas.height);

          resolve();
        };
      });

      // Add logo after background image is loaded
      await new Promise((resolve) => {
        logo.onload = () => {
          // Determine if we're on mobile
          const isMobile = window.innerWidth <= 768;

          // Calculate logo dimensions (maintain aspect ratio)
          const logoWidth = canvas.width * (isMobile ? 0.7 : 0.5); // Mobilde %70, masaüstünde %50
          const logoHeight = (logo.height * logoWidth) / logo.width;

          // Position logo (centered, 5% from the top of the image)
          const logoX = (canvas.width - logoWidth) / 2;
          const logoY = canvas.height * (isMobile ? 0.1 : 0.05); // Mobilde biraz daha aşağıda

          // Draw logo
          ctx.drawImage(logo, logoX, logoY, logoWidth, logoHeight);
          resolve();
        };
      });
    };

    loadAndProcessImage();

    // Handle window resize
    const handleResize = () => {
      loadAndProcessImage();
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <header ref={headerRef} className="header">
      <canvas ref={canvasRef} className="header-canvas" />
    </header>
  );
}

export default Header;
