import React from 'react';
import '../styles/MenuSection.css';

// Türkçe karakterler için özel toLowerCase fonksiyonu
const turkishToLowerCase = (str) => {
  const turkish = {
    'İ': 'i',
    'I': 'ı',
    'Ğ': 'ğ',
    'Ü': 'ü',
    'Ş': 'ş',
    'Ö': 'ö',
    'Ç': 'ç'
  };

  return str.replace(/[İIĞÜŞÖÇ]/g, letter => turkish[letter] || letter).toLowerCase();
};

const CalorieBadge = ({ calories }) => {
  const value = parseInt(calories);
  let badgeClass = 'calorie-badge';

  if (value > 300) {
    badgeClass += ' high';
  } else if (value >= 200) {
    badgeClass += ' medium';
  } else {
    badgeClass += ' low';
  }

  return (
    <span className={badgeClass}>
      {value} kcal
    </span>
  );
};

function MenuSection({ title, menu, isToday, isTomorrow, isOtherDay }) {
  const [dateText, ...titleParts] = title.split(' - ');
  const sectionClass = isToday 
    ? 'menu-section today' 
    : isTomorrow 
      ? 'menu-section tomorrow' 
      : 'menu-section other-day';

  const extractCalories = (item) => {
    const match = item.match(/(\d+)\s*KKAL/i);
    return match ? match[1] : null;
  };

  return (
    <section className={sectionClass}>
      <div className="menu-title">
        <h2>{turkishToLowerCase(dateText)}</h2>
        {titleParts.length > 0 && <p className="date-subtitle">{turkishToLowerCase(titleParts.join(' - '))}</p>}
      </div>
      <div className="menu-items">
        {menu.items.map((item, index) => {
          const calories = extractCalories(item);
          const itemName = turkishToLowerCase(item.replace(/\s*\d+\s*KKAL/i, ''));

          return (
            <div key={index} className="menu-item">
              <span className="item-name">{itemName}</span>
              {calories && <CalorieBadge calories={calories} />}
            </div>
          );
        })}
      </div>
    </section>
  );
}

export default MenuSection;
